package day01;

public class VariableEx3 {

	//실수형 변수 선언 예제
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 실수 리터럴 1.23의 타입은 double이다.
		// 1.23을 float에 저장하려면 접미사 F를 붙여야 한다.
		float num1 = 1.23F;
		System.out.println(num1);
		
		double num2 = 3.14;
		System.out.println(num2);
	}

}
